import { Container, Carousel, Row, Col } from "react-bootstrap";
import logo from './img/logo.svg';
import { useState, useEffect } from "react";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);
    const [carousel, setcarousel] = useState([]);

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                if (post_id == 0)
                    setpost(data);
                else
                    setpost(data.filter(p => p.post_id == post_id));
            })
            .catch(err => {
                console.log(err);
            })
    }, [post_id])


    //Call API: /carousel
    useEffect(()=>{
        fetch('http://localhost:9999/carousel')
        .then(resp=>resp.json())
        .then(data=>{
            setcarousel(data);
        })
        .catch(err=>{
            console.log(err);
        })
    },[])

    return (
        <Container>
            <Row>
                <Col xs={12} md={12} lg={12}>
                    {
                        carousel.map(ca=>(
                        <Carousel style={{ marginTop: '200px' }} key={ca.carousel_id}>
                            <Carousel.Item>
                                <img
                                    className="d-block w-100"
                                    src= {
                                        post.map(p=>p.post_id == ca.carousel_id ? p.img:'')
                                        }
                                    alt="Slide"
                                />
                                <Carousel.Caption>
                                    <h3>{
                                        post.map(p=>p.post_id == ca.carousel_id ? p.title:'')
                                        }</h3>
                                    <p>{
                                        post.map(p=>p.post_id == ca.carousel_id ? p.body:'')
                                        }</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                        </Carousel>
                        ))
                    }
                </Col>

            </Row>
        </Container>
    );
}

export default Home;
