import { useEffect, useState } from "react";
import { Col, Container, Image, Row } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";


const PostDetail = () => {


    const { pid } = useParams();

    const [p, setPost] = useState({});
    const [c, setCategory] = useState({});

    useEffect(() => {
        fetch('http://localhost:9999/posts/' + pid).then(resp => resp.json()).then(data => { setPost(data); })
    },);
    useEffect(() => {
        fetch('http://localhost:9999/category/').then(resp => resp.json()).then(data => { setCategory(data); })
    },);
    return (
        <Container>
            <Col xs={12} style={{ marginTop: '100px' }}>
                <Row style={{ justifyContent: 'left',display:'block'}}>
 
                        <h2 className="title-detail">{p.title}</h2>

                        <p><span className="author-detail">{p.author} &nbsp;</span>{<span> </span>}<span className="sub-information">{p.date}</span></p>
      
                    <Image className="coverimg" src={p.thumbnail} ></Image>
                </Row>
            </Col>
        </Container>
    );
}

export default PostDetail;
