import { Container, Carousel, Row, Col, Image, Card, Button, Pagination, Form } from "react-bootstrap";
import logo from './img/logo.svg';
import { useState, useEffect } from "react";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);
    const [carousel, setcarousel] = useState([]);

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    //Call API: /Carousel
    useEffect(() => {
        fetch('http://localhost:9999/carousel')
            .then(resp => resp.json())
            .then(data => {
                setcarousel(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    return (
        <Container fluid>
            <Row>
                <Col xs={12} md={12} lg={12}>
                    <Carousel style={{ marginTop: '100px' }}>
                        {
                            carousel.map(ca => (
                                <Carousel.Item key={ca.carousel_id}>
                                    <Image
                                        className="d-block w-100"
                                        src={ca.img}
                                        alt="First slide"
                                    />
                                    <Carousel.Caption>
                                        <h3>{post.map(p => p.post_id === ca.post_id ? p.title : '')}</h3>
                                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                            ))
                        }
                    </Carousel>
                </Col>
            </Row>
            <Row style={{ margin: '100px 0 0 50px' }}>
                <h1>News</h1>
            </Row>

            <Form>
                {

                    post.map(p => (
                        <Row style={{ margin: '50px 0 0 50px' }}>
                            <Card  key={p.post_id}>
                                <Col lg={4}>

                                    <Card.Img variant="top" src={p.img} />

                                </Col>
                                <Col lg={8}>

                                    <Card.Body>
                                        <Card.Title>Card Title</Card.Title>
                                        <Card.Text>Some quick example text to build on the card title and make up the
                                            bulk of the card's content.</Card.Text>

                                        <Button variant="primary">Go somewhere</Button>
                                    </Card.Body>

                                </Col>
                            </Card>
                        </Row>
                    ))
                }
            </Form>


            <Row style={{ margin: '50px 0 0 550px' }}>
                <Col lg={12}>
                    <Pagination>

                        <Pagination.Prev />
                        <Pagination.Item>{1}</Pagination.Item>
                        <Pagination.Item>{2}</Pagination.Item>
                        <Pagination.Item>{3}</Pagination.Item>
                        <Pagination.Ellipsis />
                        <Pagination.Last />

                    </Pagination>
                </Col>
            </Row>
        </Container >
    );
}

export default Home;
