import { useEffect, useState } from "react";
import { Col, Image, Row } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";


const PostDetail = () => {
    

    const { pid } = useParams();

    const [p, setPost] = useState({});

    useEffect(() => {
        fetch('http://localhost:9999/posts/'+pid).then(resp => resp.json()).then(data => { setPost(data); })
    },);
    return (
        <Row>
            <Col xs={12}>
                <Row>
                    <Col>
                        <h1>{p.title}</h1>
                         <Image className="coverimg" src={p.thumbnail}></Image>
                    </Col>
                </Row>
            </Col>
        </Row>
    );
}

export default PostDetail;
