import { Container, Carousel, Row, Col } from "react-bootstrap";
import logo from './img/logo.svg';
import { useState, useEffect } from "react";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                if (post_id == 0)
                    setpost(data);
                else
                    setpost(data.filter(p => p.post_id == post_id));
            })
            .catch(err => {
                console.log(err);
            })
    }, [post_id])

    return (
        <Container>
            <Row>
                <Col xs={12} md={12} lg={12}>
                    {
                        post.map(p => (
                            <Carousel style={{ marginTop: '200px' }}>
                                <Carousel.Item key={p.post_id=1}>
                                    <img
                                        className="d-block w-100"
                                        src={p.img}
                                        alt="First slide"
                                    />
                                    <Carousel.Caption>
                                        <h3>First slide label</h3>
                                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                                <Carousel.Item key={p.post_id=2}>
                                    <img
                                        className="d-block w-100"
                                        src={p.img}
                                        alt="Second slide"
                                    />

                                    <Carousel.Caption>
                                        <h3>Second slide label</h3>
                                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                                <Carousel.Item key={p.post_id=3}>
                                    <img
                                        className="d-block w-100"
                                        src={p.img}
                                        alt="Third slide"
                                    />

                                    <Carousel.Caption>
                                        <h3>Third slide label</h3>
                                        <p>
                                            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                                        </p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                            </Carousel>
                        ))
                    }
                </Col>
            </Row>
        </Container>
    );
}

export default Home;
