import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faNewspaper, faCalendarDays } from '@fortawesome/free-solid-svg-icons';
import { Card, Col, Container, Image, Pagination, Row } from 'react-bootstrap';
import { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const Category = () => {

    const [post, setpost] = useState([]);
    const [category, setcategory] = useState({});
    const [category_id,setcategory_id] = useState({});
    const {pid} = useParams();
  

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])


    //Call API: /category
    useEffect(() => {  
        fetch('http://localhost:9999/category/' + pid).then(resp => resp.json()).then(data => { 
            setcategory(data);
         })
            .catch(err => {
                console.log(err.message);
            })
    },);

    return (
        <>
            <section className='news'>
                <Container>
                    <div className="heading">
                        <h6>Category &nbsp; <FontAwesomeIcon icon={faNewspaper} style={{ color: "#ededed", }} /></h6>
                    </div>
                    {
                       category.map(c => {
                            const pst = post.find(p => p.category_id === c.category_id);
                            const thumbnail = pst ? pst.thumbnail : '';
                            const title = pst ? pst.title : '';
                            const body = pst ? pst.body : '';
                            const date = pst ? pst.date : '';
                            const author = pst ? pst.author : '';

                            return (
                                <Row className="card-wrapper" key={pid===c.category_id} style={{ padding: '10px 0 10px 0' }}>
                                    <Card>
                                        <Row style={{ padding: '10px 0 10px 0' }}>
                                            <Col xs={3} md={3} lg={3}>
                                                <Card.Img className='news-img' src={thumbnail} alt='' />
                                            </Col>
                                            <Col xs={9} md={9} lg={9}>
                                                <Row>
                                                    <h6 className='news-logo'>{c.category_name}</h6>
                                                </Row>
                                                <Row>
                                                    <h4>{title}</h4>
                                                </Row>
                                                <Row>
                                                    <p>{body.length > 50 ? `${body.substring(0, 50)}...` : body}</p>
                                                </Row>
                                                <Row>
                                                    <p className="incard-fontsize">By {author} &nbsp; <FontAwesomeIcon icon={faCalendarDays} style={{ color: "#737882", }} />&nbsp; {date} </p>
                                                </Row>
                                            </Col>
                                        </Row>
                                    </Card>
                                </Row>
                            );
                        })}
                </Container>
            </section>
        </>
    );
}

export default Category;
