import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faNewspaper } from '@fortawesome/free-solid-svg-icons';
import { Col, Container, Image, Row } from 'react-bootstrap';
import { useState, useEffect } from "react";




const News = () => {

    const [post, setpost] = useState([]);
    const [news, setnews] = useState([]);


    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])


    //Call API: /news
    useEffect(() => {
        fetch('http://localhost:9999/news')
            .then(resp => resp.json())
            .then(data => {
                setnews(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    return (
        <>
            <section className='news'>
                <div className="heading">
                    <h6>News &nbsp; <FontAwesomeIcon icon={faNewspaper} style={{ color: "#ededed", }} /></h6>
                </div>

                <Row>
                    {
                        news.map(n => {
                            const pst = post.find(p => p.post_id === n.post_id);
                            const img = pst ? pst.img : '';
                            const title = pst ? pst.title : '';

                    return (
                    <Container key={n.news_id}>
                        <Col xs={3} md={3} lg={3}>
                            <Image className='news-img' src={img} alt=''></Image>
                        </Col>
                        <Col xs={9} md={9} lg={9}>
                            <Row>
                                <h3>{title}</h3>
                            </Row>
                            <Row></Row>
                            <Row></Row>
                        </Col>
                    </Container>
                    );
                    })}
                </Row>
            </section>
        </>
    );
}

export default News;
