import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFire, faCalendarDays } from '@fortawesome/free-solid-svg-icons';
import { useState, useEffect } from "react";
import { Row, Col, Image, Card, Button } from "react-bootstrap";
import Slider from "react-slick";


const Trending = () => {

    const [post, setpost] = useState([]);


    const settings = {
        className: "center",
        centerMode: true,
        infinite: true,
        centerPadding: "60px",
        slidesToShow: 3,
        speed: 500,
        rows: 2,
        slidesPerRow: 2,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])



    return (
        <>
            <section className='trending'>
                <div className="heading">
                    <h6>Trending &nbsp; <FontAwesomeIcon icon={faFire} size="lg" style={{ color: "#f9291a", }} /></h6>
                </div>
            </section>
            <Slider {...settings}>
                <Row>
                    {
                        post.map(p => (

                            <Col lg={6} className="card-wrapper">
                                <Card className="custom-card" style={{ marginTop: '20px' }} key={p.post_id}>
                                    <Card.Img variant="top" src={p.img} />
                                    <Card.Body>
                                        <Card.Title>{p.title.length > 16 ? `${p.title.substring(0, 16)}...` : p.title}</Card.Title>
                                        <Card.Text className="text">
                                            {p.body.length > 40 ? `${p.body.substring(0, 40)}...` : p.body}
                                        </Card.Text>
                                        <p className="incard-fontsize"><FontAwesomeIcon icon={faCalendarDays} style={{ color: "#737882", }} /> {p.date} by {p.author}</p>
                                        <Button variant="dark">Click to see</Button>
                                    </Card.Body>
                                </Card>
                            </Col>

                        ))
                    }

                </Row >
            </Slider>
        </>
    );
}

export default Trending;
