import { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";


const PostDetail = () => {
    

    const { pid } = useParams();

    const [p, setPost] = useState({});

    useEffect(() => {
        fetch('http://localhost:9999/posts/'+pid).then(resp => resp.json()).then(data => { setPost(data); })
    },);
    return (
        <Row>
            <Col xs={12}>
                <Row style={{marginLeft:'200px'}}>
                    <Col>
                        <div>Id: {p.id}</div>
                        <div>title: {p.title}</div>
                        <div style={{marginTop:'30px'}}>
                            <Link to={'/'} className="btn btn-warning" style={{color:'white'}}>Back to List</Link>
                        </div>
                    </Col>
                </Row>
            </Col>
        </Row>
    );
}

export default PostDetail;
