import { Container, Carousel, Row, Col, Image } from "react-bootstrap";
import logo from './img/logo.svg';
import { useState, useEffect } from "react";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);
    const [carousel, setcarousel] = useState([]);

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    //Call API: /Carousel
    useEffect(()=>{
        fetch('http://localhost:9999/carousel')
        .then(resp=>resp.json())
        .then(data=>{
            setcarousel(data);
        })
        .catch(err=>{
            console.log(err);
        })
    },[])

        return (
            <Container>
                <Row>
                    <Col xs={12} md={12} lg={12}>
                        <Carousel style={{ marginTop: '200px' }}>
                            {
                                carousel.map(ca => (
                                    <Carousel.Item key={ca.carousel_id}>
                                        <Image
                                            className="d-block w-100"
                                            src={post.map(p => p.post_id=== ca.post_id ? ca.img:'')}
                                            alt="First slide"
                                        />
                                        <Carousel.Caption>
                                            <h3>{post.map(p => p.post_id=== ca.post_id ? p.title:'')}</h3>
                                            <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                        </Carousel.Caption>
                                    </Carousel.Item>
                                ))
                            }
                        </Carousel>



                    </Col>
                </Row>
            </Container>
        );
    }

export default Home;
