import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";



const PostDetail = () => {

    const {pid} = useParams();
    const [post, setPost] = useState({});

    useEffect(() => {
        fetch('http://localhost:9999/posts/'+ pid).then (resp => resp.json()).then(data => {setPost(data);})
    }, );

    return (
        <>
            <main>
                <div className='container'>
                    <section className='mainContent details'>
                        <h1 className='title'>{post.title}</h1>

                        <div className='author'>
                            <span>by</span>
                            <p> {post.author} on</p>
                            <label>{post.date}</label>
                        </div>

                        <div className='desctop'>
                            {post.body.map((val) => {
                                return (
                                    <>
                                        <p>{val.para1}</p>
                                    </>
                                )
                            })}
                        </div>
                        <img src={post.thumbnail} alt='' />
                        {post.body.map((val) => (
                            <p>{val.para3}</p>
                        ))}

                        <div className='descbot'>
                            {/* {post.details.map((data) => {
                                return (
                                    <>
                                        <h1>{data.title}</h1>
                                        <p>{data.para1}</p>
                                    </>
                                )
                            })} */}
                        </div>

                        <div className='quote'>
                            <i className='fa fa-quote-left'></i>
                            {/* {post.details.map((data) => (
                                <p>{data.quote}</p>
                            ))} */}
                        </div>

                        <div className='desctop'>
                            {/* {post.details.map((data) => {
                                return (
                                    <>
                                        <p>{data.para2}</p>
                                        <p>{data.para3}</p>
                                    </>
                                )
                            })} */}
                        </div>
                    </section>
                    <section className='sideContent'>
                        
                    </section>
                </div>
            </main>
        </>
    );
}

export default PostDetail;
