import { Container, Carousel, Row, Col, Image, Card, Button, Pagination } from "react-bootstrap";
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarDays } from '@fortawesome/free-solid-svg-icons';

import Trending from "./Trending";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);
    const [carousel, setcarousel] = useState([]);
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex) => {
        setIndex(selectedIndex);
    };


    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    //Call API: /Carousel
    useEffect(() => {
        fetch('http://localhost:9999/carousel')
            .then(resp => resp.json())
            .then(data => {
                setcarousel(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    return (
        <Container fluid>
            <Row>
                <Col xs={12} md={12} lg={12}>
                    <Carousel style={{ marginTop: '100px' }} activeIndex={index} onSelect={handleSelect}>
                        {
                            carousel.map(ca => (
                                <Carousel.Item key={ca.carousel_id}>
                                    <Image
                                        className="d-block w-100"
                                        src={ca.img}
                                        alt="First slide"
                                    />
                                    <Carousel.Caption>
                                        <h3>{post.map(p => p.post_id === ca.post_id ? p.title : '')}</h3>
                                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                            ))
                        }
                    </Carousel>
                </Col>
            </Row>
            <Row>
                <Col lg={9}>
                    <Trending />
                    <Row>

                        {
                            post.map(p => (
                                <Col lg={3} className="card-wrapper">
                                    <Card className="custom-card" style={{ marginTop: '20px' }} key={p.post_id}>
                                        <Card.Img variant="top" src={p.img} />
                                        <Card.Body>
                                            <Card.Title>{p.title.length > 16 ? `${p.title.substring(0, 16)}...` : p.title}</Card.Title>
                                            <Card.Text className="text">
                                                {p.body.length > 40 ? `${p.body.substring(0, 40)}...` : p.body}
                                            </Card.Text>
                                            <p className="incard-fontsize"><FontAwesomeIcon icon={faCalendarDays} style={{ color: "#737882", }} /> {p.date} by {p.author}</p>
                                            <Button variant="dark">Click to see</Button>
                                        </Card.Body>
                                    </Card>
                                </Col>
                            ))
                        }

                    </Row>
                </Col>

                <Col lg={3}>
                    <Trending />
                </Col>
            </Row>

        </Container >
    );
}

export default Home;
