import { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";



const PostDetail = () => {

    const { pid } = useParams();
    const [p, setPost] = useState({});

    useEffect(() => {
        fetch('http://localhost:9999/posts/' + pid).then(resp => resp.json()).then(data => { setPost(data); })
    },);

    return (
        <>
            <Container>
                <Row>
                    <Col>
                        <h1>{p.title}</h1>
                    </Col>
                </Row>
            </Container>
        </>
    );
}

export default PostDetail;
