import { Container, Carousel, Row, Col, Image, Card, Button, Pagination } from "react-bootstrap";
import { useState, useEffect } from "react";
import Trending from "./Trending";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);
    const [carousel, setcarousel] = useState([]);

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                setpost(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    //Call API: /Carousel
    useEffect(() => {
        fetch('http://localhost:9999/carousel')
            .then(resp => resp.json())
            .then(data => {
                setcarousel(data);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    return (
        <Container fluid>
            <Row>
                <Col xs={12} md={12} lg={12}>
                    <Carousel style={{ marginTop: '100px' }}>
                        {
                            carousel.map(ca => (
                                <Carousel.Item key={ca.carousel_id}>
                                    <Image
                                        className="d-block w-100"
                                        src={ca.img}
                                        alt="First slide"
                                    />
                                    <Carousel.Caption>
                                        <h3>{post.map(p => p.post_id === ca.post_id ? p.title : '')}</h3>
                                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                            ))
                        }
                    </Carousel>
                </Col>
            </Row>
            <Trending/>
            <Row style={{ margin: '50px 0 0 50px' }}>

                {
                    post.map(p => (
                        <Col lg={3}>
                            <Card style={{ width: '18rem',marginTop:'20px' }} key={p.post_id}>
                                <Card.Img variant="top" src={p.img} />
                                <Card.Body>
                                    <Card.Title>{p.title.length > 20 ? `${p.title.substring(0, 20)}...` : p.title}</Card.Title>
                                    <Card.Text>
                                        Some quick example text to build on the card title and make up the
                                        bulk of the card's content.
                                    </Card.Text>
                                    <Button variant="dark">Click to see</Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))
                }

            </Row>


        </Container >
    );
}

export default Home;
