import { Container, Carousel, Row, Col } from "react-bootstrap";
import logo from './img/logo.svg';
import { useState, useEffect } from "react";

const Home = () => {

    const [post_id, setpost_id] = useState(0);
    const [post, setpost] = useState([]);
    const [hotpost, sethotpost] = useState([]);

    //Call API: /posts
    useEffect(() => {
        fetch('http://localhost:9999/posts')
            .then(resp => resp.json())
            .then(data => {
                if (post_id == 0)
                    setpost(data);
                else
                    setpost(data.filter(p => p.post_id == post_id));
            })
            .catch(err => {
                console.log(err);
            })
    }, [post_id])


    //Call API: /hotposts
    useEffect(() => {
        fetch('http://localhost:9999/hotposts')
            .then(resp => resp.json())
            .then(data => {
                if (post_id == 0)
                    setpost(data);
                else
                    setpost(data.filter(p => p.post_id == post_id));
            })
            .catch(err => {
                console.log(err);
            })
    }, [post_id])

    return (
        <Container>
            <Row>
                <Col xs={12} md={12} lg={12}>
                    {
                        hotpost.map(hp=>(
                        <Carousel style={{ marginTop: '200px' }} key={hp.hotpost_id}>
                            <Carousel.Item>
                                <img
                                    className="d-block w-100"
                                    src= {
                                        post.map(p=>p.post_id == hp.post_id ? p.image:{})
                                        }
                                    alt="Slide"
                                />
                                <Carousel.Caption>
                                    <h3>{hp.hottitle}</h3>
                                    <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                        </Carousel>
                        ))
                    }
                </Col>

            </Row>
        </Container>
    );
}

export default Home;
